import { useForm } from "@inertiajs/vue3";
const form = useForm({
    email: "",
    password: "",
    remember: null,
});
const submit = () => {
    form.post(route("login.store"), {
        onFinish: () => form.reset("password"),
    });
};
defineProps({
    status: String,
});
